import 'package:flutter/material.dart';
import 'package:webview_flutter/webview_flutter.dart';
import 'dart:io';
import 'package:http_server/http_server.dart';
import 'package:path/path.dart' as p;
import 'dart:async';
import 'package:flutter_linux_webview/flutter_linux_webview.dart';


class AvatarPage extends StatefulWidget {
  const AvatarPage({super.key});

  @override
  _AvatarState createState() => _AvatarState();
}

class _AvatarState extends State<AvatarPage> {
  HttpServer? _server;
  late final WebViewController _controller;

  @override
  void initState() {
    super.initState();
    // 在頁面初始化時啟動伺服器
    WidgetsFlutterBinding.ensureInitialized();

    // 初始化 Linux WebView 插件，並禁用 GPU 以避免驅動程式問題
    LinuxWebViewPlugin.initialize(options: {
      'user-agent': 'Flutter Linux WebView',
      'remote-debugging-port': '8888',
      'autoplay-policy': 'no-user-gesture-required',
      'disable-gpu': '',
      'disable-gpu-sandbox': '',
      // 'enable-gpu': '',
      // 'enable-webgl': '',
      // 'ignore-gpu-blocklist': '',
      // 'use-gl': 'desktop',
    });

    // 設定 WebView 平台為 Linux
    WebView.platform = LinuxWebView();
    startServer();
  }

  @override
  void dispose() {
    // 在頁面被銷毀時關閉伺服器
    _server?.close(force: true);
    print('Local server stopped.');
    super.dispose();
  }

  // 將伺服器啟動邏輯放在此處
  Future<HttpServer> startServer() async {
    // 伺服器將在 127.0.0.1:8080 上運行
    final server = await HttpServer.bind(InternetAddress.loopbackIPv4, 8080);
    final staticFiles = VirtualDirectory(
      p.join(Directory.current.path, 'assets', 'web'),
    )
      ..allowDirectoryListing = true
      ..jailRoot = false;

    server.listen((HttpRequest request) {
      // 處理 CORS 相關的標頭，允許 WebView 載入資源
      request.response.headers.add('Access-Control-Allow-Origin', '*');
      staticFiles.serveRequest(request);
    });

    print('Local server started on http://127.0.0.1:8080');
    return server;
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: const Text('Local HTML WebView')),
      body: Stack(
      children: [

        WebView(
          initialUrl: 'http://127.0.0.1:8080/index.html', // 先載入空白頁，等伺服器啟動後再載入
          javascriptMode: JavascriptMode.unrestricted,
          debuggingEnabled: true,
          onWebViewCreated: (WebViewController controller) {
            _controller = controller;
          },
          onPageFinished: (String url) {
            if (url != 'about:blank') {
              print('onPageFinished: $url');
            }
          },
        ),
        Positioned(
          bottom: 20,
          left: 20,
          child: ElevatedButton(
            onPressed: () {

              // 點擊按鈕時呼叫此方法，並傳遞一個字串
              callTtsResult('{"tts_sentence":" \u4e0d\u904e\u6211\u53ef\u4ee5\u544a\u8a34\u4f60\u7576\u524d\u7684\u5929\u6c23\u72c0\u6cc1\uff0c","tts_audio":"","response_id":"Response_04efdf7631c4","index":1,"total":6,"align_result":[{"word":"\u4e0d","start":0.4,"end":0.56},{"word":"\u904e","start":0.56,"end":0.76},{"word":"\u6211","start":1.16,"end":1.32},{"word":"\u53ef","start":1.32,"end":1.36},{"word":"\u4ee5","start":1.36,"end":1.48},{"word":"\u544a","start":1.48,"end":1.64},{"word":"\u8a34","start":1.64,"end":1.8},{"word":"\u4f60","start":1.8,"end":2.0},{"word":"\u7576","start":2.0,"end":2.2},{"word":"\u524d","start":2.2,"end":2.4},{"word":"\u7684","start":2.4,"end":2.6},{"word":"\u5929","start":2.68,"end":2.84},{"word":"\u6c23","start":2.84,"end":3.0},{"word":"\u72c0","start":3.0,"end":3.2},{"word":"\u6cc1","start":3.2,"end":3.4},{"word":"\uff0c","start":3.4,"end":3.6}]}');
            },
            child: const Text('send text WebView'),
          ),
        ),
      ]
      )
    );
  }

  void callTtsResult(String text) {
    print('callTtsResult = $text');
    // 將 Dart 字串轉換為 JavaScript 字串
    String jsCode = "document.getElementById('avatar').ttsResult = $text;";
    _controller.runJavascript(jsCode);
  }
}